mapper.select:
select
  id, user_id, telecommand_name, equipment_id, parameter, time, operation_feedback
from mcs.t_telecommand_log


        and id = #{id}


        and user_id = #{userId}


        and telecommand_name = #{telecommandName}


        and equipment_id = #{equipmentId}


        and parameter = #{parameter}


        and time = #{time}


        and operation_feedback = #{operationFeedback}
